{% raw %}
<script type="text/javascript">
	(function(){
		var books = document.querySelectorAll('.books .book');
		function fadeAll(){
			for(let b of books){
				if(this !== b){
					b.classList.toggle('grayscale');
				}	
			}
		}

		books.forEach(function(book){
			book.onmouseenter = fadeAll.bind(book)
			book.onmouseleave = fadeAll.bind(book);
		});
	}());
</script>
{% endraw %}

