        this.body.moveTo(this.body.x,  this.body.y - 100);
    }

    down(){

        this.body.shape.style.transition = "all ease-in 1s";
        this.body.moveTo(this.body.x,  this.body.y + 100);
    }
}
export default Bounce;
```

